/**
 * Initializes new instance of User object
 * @param data
 * @constructor
 */
function User(data) {

    this.id = data.UserId;
    this.email = data.Email;
    this.roles = data.Roles || ['anonymous'];
    this.displayName = data.DisplayName || this.email;
    if (data.XmppSession) {
        this.xmppSession = {
            sid: data.XmppSession.Sid,
            jid: data.XmppSession.Jid,
            rid: data.XmppSession.Rid
        };
    }
}
