angular
    .module('whatwanted.AuthorizationModule', ['whatwanted.Storage', 'whatwanted.ApiModule'])
    .config(['$httpProvider', function ($httpProvider) {
        $httpProvider.interceptors.push('authorizationHeaderInterceptor');
        $httpProvider.interceptors.push('ticketExpirationInterceptor');
    }]);